import React, { useEffect, useState } from 'react';
import './Home.scss'; // Importing the SCSS file
import axios from 'axios';

const Home: React.FC = () => {

    const [brands, setBrands] = useState<any[]>([]); // Brands data
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);
  
    useEffect(() => {
      // Fetch the brands data from the backend API
      const fetchBrands = async () => {
        try {
            const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/brands`);
          setBrands(response.data); // Set the brands data
        } catch (err) {
          setError('Error fetching brands');
          console.error(err);
        } finally {
          setLoading(false);
        }
      };
  
      fetchBrands();
    }, []);
  
    if (loading) {
      return <div>Loading brands...</div>;
    }
  
    if (error) {
      return <div>{error}</div>;
    }
    
  return (
    <div>
      {/* Header Section */}
      <header className="header">
        <div className="logo">Live with Confidence</div>
        <button className="cta-btn">Get Started</button>
      </header>

      {/* About Us Section */}
      <section className="about-us">
        <h2>What we do</h2>
        <p>We offer a complete range of bespoke design and development services to help you turn your ideas into digital masterpieces.</p>
        <div className="services">
          <div className="service">
            <h3>Web Development</h3>
          </div>
          <div className="service">
            <h3>User Experience & Design</h3>
          </div>
          <div className="service">
            <h3>Mobile App Development</h3>
          </div>
          <div className="service">
            <h3>Blockchain Solutions</h3>
          </div>
        </div>
      </section>

      {/* Case Studies Section */}
      <section className="case-studies">
        <h2>Case Studies</h2>
        <div className="case-study">
          <h3>The Olympian</h3>
        </div>
        <div className="case-study">
          <h3>The Saving Jar</h3>
        </div>
        <div className="case-study">
          <h3>Skhokho eMail</h3>
        </div>
      </section>

      {/* Trusted Brands Section */}
      <section className="brands">
        <h2>Trusted by Leading Brands</h2>
        <div className="brand-container">
        {brands.map((brand) => (
          <div key={brand._id} className="brand-card">
            <img src={brand.logoUrl} alt={brand.name} className="brand-logo" />
            <p>{brand.name}</p>
          </div>
          ))}
          </div>
      </section>

      {/* Footer Section */}
      <footer className="footer">
        <h2>Have a project in mind? Let's make it happen.</h2>
        <p>Terms of Service | Privacy Policy | Contact Us</p>
        <p>Â© 2023 Your Company Name</p>
      </footer>
    </div>
  );
};

export default Home;
